    <style>
        /* Estilos globales */
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            line-height: 1.6;
            color: #333;
        }

        /* Menú fijo con efecto blur */
        header {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            background: rgba(0, 0, 0, 0.8); /* Fondo semitransparente */
            backdrop-filter: blur(12px); /* Efecto blur */
            z-index: 5;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.2);
        }

        header .container {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 1rem;
        }

        header h1 {
            font-size: 1.5rem;
            font-weight: bold;
            color: #fff;
        }

        header nav ul {
            list-style: none;
            display: flex;
            gap: 1rem;
        }

        header nav ul li a {
            text-decoration: none;
            color: #fff;
            font-weight: bold;
            transition: color 0.3s ease;
        }

        header nav ul li a:hover {
            color: #00f0ff;
        }

        /* Sección Hero con fondo dinámico */
        .hero {

            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            background: linear-gradient(30deg,rgb(0, 0, 6),rgb(0, 0, 0),rgba(12, 1, 61, 0.56),rgb(0, 0, 0),rgb(0, 0, 0));
            background-size: 400% 400%; /* Tamaño del gradiente */
            animation: gradientAnimation 15s ease infinite; /* Animación del gradiente */
            color: #fff; /* Texto blanco sobre fondo oscuro */
        }

        @keyframes gradientAnimation {
            0% {
                background-position: 0% 50%;
            }
            50% {
                background-position: 100% 50%;
            }
            100% {
                background-position: 0% 50%;
            }
        }

        .hero h2 {
            font-size: 3rem;
            margin-bottom: 1rem;
        }

        .hero p {
            font-size: 1.25rem;
            margin-bottom: 2rem;
        }

        .hero .btn {
            padding: 1rem 2rem;
            font-size: 1rem;
        }
        .hero svg {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 120%;
            opacity: 1; /* Transparencia del SVG */
            background: black;
            z-index: -1;
        }
        /* animacion del borde del formulario */
        .animate-border-spin {
            content: '';
            position: absolute;
            top: -2px;
            left: -2px;
            right: -2px;
            bottom: -2px;
            z-index: -1;
            border-radius: inherit;
            background: conic-gradient(from 0deg, transparent, blue, transparent);
            animation: spin 3s linear infinite;
        }

        /* Animación de color en las líneas del circuito */
        .circuit-line {
            stroke: #00ffff;
            stroke-opacity: 1;
            animation: pulseColor 5s infinite alternate;
        }
        @keyframes pulseColor {
            0% {
                stroke: #00ffff;
            }
            100% {
                stroke: #ff00ff;
            }
        }
        /* Botón flotante de WhatsApp */
        .whatsapp-button {
            position: fixed;
            bottom: 20px;
            right: 20px;
            background-color: #25d366;
            color: white;
            width: 50px;
            height: 50px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            text-decoration: none;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.2);
            transition: background-color 0.3s ease;
        }

        .whatsapp-button:hover {
            background-color: #128c7e;
        }

        .whatsapp-button img {
            width: 30px;
            height: 30px;
        }
    </style>
</head>
